var title_f36_51_37691="Lynch syndr CGC guidelines";
var content_f36_51_37691=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F82293&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F82293&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Lynch syndrome: Cancer Genetics Consortium management guidelines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\" colspan=\"2\">",
"       Intervention",
"      </td>",
"      <td class=\"subtitle1\" rowspan=\"2\">",
"       Recommendation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2\">",
"       Men and women",
"      </td>",
"      <td class=\"subtitle2\">",
"       Women",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       History and physical exam",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Every year beginning at age 21 with review of systems, education, and counseling regarding Lynch syndrome",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Colonoscopy",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Every 1-2 years beginning at age 20-25 years or 10 years younger than the youngest age at diagnosis in the family, whichever comes first.  For MSH6 families, begin at age 30.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Colorectal resection",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Generally not recommended for primary prophylaxis, but if cancer diagnosed, subtotal colectomy is favored",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Transvaginal ultrasound",
"      </td>",
"      <td>",
"       Every year beginning at age 30-35 years",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Hysterectomy with bilateral salpingo-oophorectomy",
"      </td>",
"      <td>",
"       Offer as option after childbearing complete",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Urinalysis with cytology",
"      </td>",
"      <td>",
"       &nbsp;",
"      </td>",
"      <td>",
"       Every 1-2 years beginning at age 25-35 years",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Lindor, NM, Petersen, GM, Hadley, DW, et al. Recommendations for the Care of Individuals With an Inherited Predisposition to Lynch Syndrome: A Systematic Review. JAMA 2006; 296:1507.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
