" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Intermediate- and long-term mechanical cardiac support",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/57/37785/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?36/57/37785/contributors\">",
"     Emma Jane Birks, MD, PhD, FRCP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/57/37785/contributors\">",
"     Section Editors",
"    </a>",
