var title_f36_57_37788="Pediatric ambulance equipment for basic life support";
var content_f36_57_37788=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F82004&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F82004&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pediatric equipment for basic life support (BLS) ambulances in the United States",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       BLS ambulances",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Required*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_single\">",
"       Airway and ventilation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Portable and fixed suction with regulator and wide bore suction tubing",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Suction catheters: tonsil-tip and flexible (1 flexible between 6 and 10 F, 1 between 10 F and 16 F)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Fixed and portable metered flow oxygen and oxygen tubing with variable flow regulator",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Oxygen masks (valveless and nonrebreathing): adult and child sizes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Nasal cannulas: adult and child sizes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Bag-valve-mask resuscitator: hand operated, self-reexpanding bag for adult (&ge;1000 mL) and child (450-750 mL) with oxygen reservoir/accumulator, valve, and mask (neonatal, infant, child, and adult sizes)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Airways: oropharyngeal (sizes 0-5); nasopharyngeal (16 F-34 F, adult and child sizes)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Pulse oximeter with pediatric and adult probes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Saline drops and infant nasal bulb syringe",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_single\">",
"       Monitoring and defibrillation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Automatic external defibrillator with adult and pediatric pads and cables",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_single\">",
"       Immobilization devices",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Rigid cervical collars for children aged 2 years or older",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Head immobilization device, eg, firm padding or commercial device (not sand bags)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Lower extremity (femur) traction devices",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Upper and lower extremity immobilization devices for children",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Backboards",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_single\">",
"       Obstetrical kit",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Optional basic pediatric equipment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Infant oxygen mask",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Infant self-inflating resuscitation bag",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Infant airways (nasopharyngeal 12 F, 14 F; oropharyngeal size 00)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       BP cuffs, neonatal and infant",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Pediatric stethoscope",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     F: French.",
"     <br>",
"      * In addition to standard equipment and supplies as determined by state and local regulations.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Policy statement--Equipment for ambulances. American College of Surgeons Committee on Trauma, American College of Emergency Physicians, National Association of EMS Physicians, Pediatric Equipment Guidelines Committee - Emergency Medical Services for Children (EMSC) Partnership for Children Stakeholder Group, American Academy of Pediatrics. Pediatrics 2009; 124:e166.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
