var title_f36_6_36973="Seizures versus syncope";
var content_f36_6_36973=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F73515&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F73515&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differentiation of generalized tonic-clonic seizures from pseudoseizures and syncope",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Characteristic",
"       </td>",
"       <td class=\"subtitle1\">",
"        Generalized seizure tonic-clonic",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pseudoseizure",
"       </td>",
"       <td class=\"subtitle1\">",
"        Syncope",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Circumstances",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Situation",
"       </td>",
"       <td>",
"        Awake or sleep",
"       </td>",
"       <td>",
"        Awake",
"       </td>",
"       <td>",
"        Usually upright; any position if cardiogenic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Precipitating factors",
"       </td>",
"       <td>",
"        Sleep loss, alcohol withdrawal, flashing lights",
"       </td>",
"       <td>",
"        Emotion",
"       </td>",
"       <td>",
"        Emotion, injury, heat, crowds; none if cardiogenic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Presence of others",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"       <td>",
"        Usual",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Motor phenomena",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vocalization",
"       </td>",
"       <td>",
"        At onset, if any",
"       </td>",
"       <td>",
"        During course",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Location of motor component (if present)",
"       </td>",
"       <td>",
"        Proximal limb",
"       </td>",
"       <td>",
"        Proximal limb",
"       </td>",
"       <td>",
"        None",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Generalized motor",
"       </td>",
"       <td>",
"        Tonic, then clonic",
"       </td>",
"       <td>",
"        Tonic; flailing; struggling or thrashing, or both",
"       </td>",
"       <td>",
"        Usually atonic; if syncope lasts &gt;20 seconds: tonic, then clonic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tonic posture",
"       </td>",
"       <td>",
"        Partial flexion or straight",
"       </td>",
"       <td>",
"        Opisthotonic",
"       </td>",
"       <td>",
"        -",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Head movements",
"       </td>",
"       <td>",
"        To one side or none",
"       </td>",
"       <td>",
"        Side to side",
"       </td>",
"       <td>",
"        -",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Clonus/limb jerks",
"       </td>",
"       <td>",
"        Bilaterally synchronous",
"       </td>",
"       <td>",
"        Asynchronous",
"       </td>",
"       <td>",
"        Bilaterally synchronous",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Purposeful movements",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"       <td>",
"        Occasional, including avoidance",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Biting",
"       </td>",
"       <td>",
"        Tongue, inside mouth",
"       </td>",
"       <td>",
"        Lips, arms, other people",
"       </td>",
"       <td>",
"        Tongue biting rare",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Babinski's sign",
"       </td>",
"       <td>",
"        Present",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"       <td>",
"        Absent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Autonomic features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Micturition",
"       </td>",
"       <td>",
"        Frequent",
"       </td>",
"       <td>",
"        Rare",
"       </td>",
"       <td>",
"        Occasional",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Eyes",
"       </td>",
"       <td>",
"        Open",
"       </td>",
"       <td>",
"        Closed",
"       </td>",
"       <td>",
"        Open",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pupils",
"       </td>",
"       <td>",
"        Dilated or hippus during attacks",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Dilated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Colour",
"       </td>",
"       <td>",
"        Cyanotic or grey",
"       </td>",
"       <td>",
"        Rubor or normal",
"       </td>",
"       <td>",
"        Pale",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pulse",
"       </td>",
"       <td>",
"        Rapid, strong",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Slow if vasovagal, weak if vasodepressor; that of arrhythmias if cardiogenic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Timing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Usual duration",
"       </td>",
"       <td>",
"        1 to 5 min",
"       </td>",
"       <td>",
"        5 to 60 min",
"       </td>",
"       <td>",
"        1 to 2 min",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Onset",
"       </td>",
"       <td>",
"        Sudden",
"       </td>",
"       <td>",
"        Gradual",
"       </td>",
"       <td>",
"        Gradual; possibly sudden if cardiogenic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sequence of symptoms",
"       </td>",
"       <td>",
"        Stereotyped",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"       <td>",
"        Stereotyped",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Termination",
"       </td>",
"       <td>",
"        Spontaneous",
"       </td>",
"       <td>",
"        Spontaneous or induced by supraorbital pressure, suggestion",
"       </td>",
"       <td>",
"        Rapid",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Sequelae",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Injury",
"       </td>",
"       <td>",
"        Frequent, mild; scalp, face, common",
"       </td>",
"       <td>",
"        Rare, but multiple bruises possible; scalp, face, rare",
"       </td>",
"       <td>",
"        If sudden onset",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Postictal",
"       </td>",
"       <td>",
"        Tired, confused, sleepy",
"       </td>",
"       <td>",
"        Alert, emotional outburst",
"       </td>",
"       <td>",
"        Regains consciousness in 2 to 3 min; alert but tired",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Blume WT. Diagnosis and management of epilepsy. CMAJ 2003; 168:441. Copyright &copy; 2003 Canadian Medical Association.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
