" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Overview of chronic daily headache",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/60/37830/contributors\">",
