" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Pharmacotherapy of allergic rhinitis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/60/37834/contributors\">",
