var title_f36_61_37852="Cardiac involvement in vasculitis";
var content_f36_61_37852=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F79462&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F79462&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Principle features and cardiovascular/pericardial involvement in vasculitides",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Vasculitis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Principle features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Estimated frequency of cardiac/pericardial involvement (percent)",
"       </td>",
"       <td class=\"subtitle1\">",
"        Type of cardiac/pericardial involvement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        Takayasu arteritis",
"       </td>",
"       <td rowspan=\"2\">",
"        Chronic inflammatory and obliterative disease of the aorta and its major branches.",
"       </td>",
"       <td>",
"        Cardiac: rare",
"       </td>",
"       <td>",
"        Fibrosis, narrowing and aneurysm of the aorta, its branches, and pulmonary arteries.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pericardial: rare (case reports)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Giant cell arteritis",
"       </td>",
"       <td rowspan=\"2\">",
"        Vasculitis that affects large and medium-sized blood vessels (especially cranial arteries branching from the carotid artery).",
"       </td>",
"       <td>",
"        Cardiac: &lt;10",
"       </td>",
"       <td>",
"        Aneurysm, coronary arteritis, myocardial infarction.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pericardial: rare (case reports)",
"       </td>",
"       <td>",
"        Pericarditis, pericardial effusion.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Polyarteritis nodosa",
"       </td>",
"       <td rowspan=\"2\">",
"        Necrotizing arteritis involving medium-sized and small-sized arteries with inflammation and ischemia of affected organs.",
"       </td>",
"       <td>",
"        Cardiac: &lt;50",
"       </td>",
"       <td>",
"        Hypertension (renal vasculitis), aneurysm, and myocardial infarction.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pericardial: rare (case reports, limited series)",
"       </td>",
"       <td>",
"        Pericarditis.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Kawasaki disease",
"       </td>",
"       <td rowspan=\"2\">",
"        Systemic necrotizing medium-sized vessel vasculitis mainly affecting blood vessels, skin, mucous membranes and lymph nodes.",
"       </td>",
"       <td>",
"        Cardiac: 30",
"       </td>",
"       <td>",
"        Coronary arteritis and aneurysm, myocardial infarction, myocarditis, heart failure.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pericardial: 30",
"       </td>",
"       <td>",
"        Pericardial effusion, pericarditis.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Churg-Strauss syndrome",
"       </td>",
"       <td rowspan=\"2\">",
"        Systemic necrotizing vasculitis affecting small-sized to medium-sized vessels, characterized by asthma, hypereosinophilia, and eosinophil-rich granulomatous inflammation of the respiratory tract.",
"       </td>",
"       <td>",
"        Cardiac: &gt;50",
"       </td>",
"       <td>",
"        Eosinophilic endomyocarditis, heart failure, heart block.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pericardial: 25-50",
"       </td>",
"       <td>",
"        Pericarditis, pericardial effusion.",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Granulomatosis with polyangiitis",
"       </td>",
"       <td rowspan=\"2\">",
"        Necrotizing vasculitis that mainly affects arteries in the respiratory tract and kidneys.",
"       </td>",
"       <td>",
"        Cardiac: 10-40",
"       </td>",
"       <td>",
"        Coronary arteritis, valve lesions, heart block, aortic aneurysm.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pericardial: &lt;10",
"       </td>",
"       <td>",
"        Pericarditis, pericardial effusion.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
