" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Effect of oral contraceptives and postmenopausal hormone therapy on blood pressure",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/62/37859/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?36/62/37859/contributors\">",
"     Norman M Kaplan, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/62/37859/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?36/62/37859/contributors\">",
"     George L Bakris, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/62/37859/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?36/62/37859/contributors\">",
"     John P Forman, MD, MSc",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
