"     </li>",
"     <li>",
"      Doxet (PE);",
"     </li>",
"     <li>",
"      Duloxeteg (SV);",
"     </li>",
"     <li>",
"      Duxetin (UY);",
"     </li>",
"     <li>",
"      Xeristar (AT, BE, BG, CH, CZ, DE, DK, EE, ES, FI, FR, GB, HN, IE, IT, MT, NL, NO, PL, RU, SE, SK, TR);",
"     </li>",
"     <li>",
"      Yentreve (AT, BE, BG, CH, CZ, DE, DK, EE, ES, FI, FR, GB, HN, IE, IL, IT, MT, MX, NL, NO, NZ, PL, RU, SE, SK, TR)",
"     </li>",
