var title_f36_62_37867="Causes subclin hypothyroid";
var content_f36_62_37867=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F73266&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F73266&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of subclinical hypothyroidism",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Chronic autoimmune thyroiditis (risk factors: family history of autoimmune thyroid disease, personal or family history of associated autoimmune disorders, Down syndrome, Turner's syndrome)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent TSH increase in subacute thyroiditis, postpartum thyroiditis, painless thyroiditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thyroid injury: partial thyroidectomy or other neck surgery, radioactive iodine therapy, external radiotherapy of the head and neck",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Drugs impairing thyroid function: iodine and iodine-containing medications (amiodarone, radiographic contrast agents), lithium carbonate, cytokines (especially interferon &alpha;), aminoglutetimide, ethionamide, sulfonamides, and sulfonylureas",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Inadequate replacement therapy for overt hypothyroidism (inadequate dosage, noncompliance, drug interactions [iron, calcium carbonate, cholestyramine, dietary soy, fiber, etc.], increased T",
"        <sub>",
"         4",
"        </sub>",
"        clearance [phenytoin, carbamazepine, phenobarbital, etc.], malabsorption)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Thyroid infiltration (amyloidosis, sarcoidosis, hemochromatosis, Riedel's thyroiditis, cystinosis, AIDS, primary thyroid lymphoma)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Central hypothyroidism with impaired TSH bioactivity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Toxic substances, industrial and environmental agents",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        TSH receptor gene mutations; G&alpha; gene mutations",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Biondi B, Cooper DS. The clinical significance of subclinical thyroid dysfunction. Endocrine Reviews 2008; 29:76. file://edrv.endojournals.org. Copyright &copy; 2008 The Endocrine Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
