" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of bone metastases in advanced prostate cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?36/8/36999/contributors\">",
