" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Transtracheal oxygen therapy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
