" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical features of primary aldosteronism",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/1/37911/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/1/37911/contributors\">",
"     William F Young, Jr, MD, MSc",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/1/37911/contributors\">",
"     Norman M Kaplan, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/1/37911/contributors\">",
"     Burton D Rose, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/1/37911/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/1/37911/contributors\">",
"     Andre Lacroix, MD",
"    </a>",
"    <br/>",
