var title_f37_1_37916="Prevention transmission HBV";
var content_f37_1_37916=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F82151%7EGAST%2F55845&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F82151%7EGAST%2F55845&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for infected persons regarding prevention of transmission of HBV to others",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Persons who are HBsAg-positive should:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have sexual contacts vaccinated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use barrier protection during sexual intercourse if partner not vaccinated or naturally immune",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Not share toothbrushes or razors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cover open cuts and scratches",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Clean blood spills with detergent or bleach",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Not donate blood, organs or sperms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Children and adults who are HBsAg-positive:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Can participate in all activities including contact sports",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Should not be excluded from daycare or school participation and should not be isolated from other children",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Can share food, utensils, or kiss others",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Lok ASF, McMahon BJ. Chronic hepatitis B: Update 2009. Hepatology 2009; 50:661. Available online at file://publish.aasld.org/Pages/Default.aspx. Accessed September 8th 2009. Copyright &copy; 2009 American Association for the Study of Liver Diseases.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations for counseling and prevention of transmission of hepatitis B from individuals with chronic hepatitis B virus (HBV) infection:",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Carriers should be counseled regarding prevention of transmission of HBV",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Sexual and household contacts of carriers who are negative for HBV seromarkers should receive hepatitis B vaccination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Newborns of HBV-infected mothers should receive HBIG and hepatitis B vaccine at delivery and complete the recommended vaccination series",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Persons who remain at risk for HBV infection such as infants of HBsAg-positive mothers, health care workers, dialysis patients, and sexual partners of carriers should be tested for response to vaccination",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Postvaccination testing should be performed at 9 to 15 months of age in infants of carrier mothers and one to two months after the last dose in other persons",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       &bull; Follow-up testing of vaccine responders is recommended annually for chronic hemodialysis patients",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Abstinence or only limited use of alcohol is recommended in hepatitis B carriers",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Persons who are positive only for anti-HBc and who are from a low endemic area with no risk factors for HBV should be given the full series of hepatitis B vaccine",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Lok, ASF, McMahon, BJ. Chronic hepatitis B: Update 2009. Hepatology 2009; 50:661. Available online at file://publish.aasld.org/Pages/Default.aspx. Accessed September 8, 2009.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
