var title_f37_11_38075="Cardiac cath chronic MR";
var content_f37_11_38075=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F55552&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F55552&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA guideline summary: Cardiac catheterization in patients with chronic mitral regurgitation (MR)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class I - There is evidence and/or general",
"agreement that cardiac catheterization is indicated in patients with",
"chronic MR in the following settings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; For left ventriculography and hemodynamic measurements when noninvasive tests are not conclusive with respect to the severity of MR, left ventricular function, or the need for surgery.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; For hemodynamic measurements when, on noninvasive testing, the pulmonary artery pressure is out of proportion to severity of MR.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; For left ventriculography and hemodynamic measurements when there is a discrepancy regarding the severity of MR between clinical and noninvasive findings.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; For coronary angiography before mitral valve surgery (repair or valve replacement) in patients at risk for coronary artery disease (eg, older age, hypercholesterolemia, hypertension) or with suspected ischemic MR.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Class III - There is evidence and/or",
"general agreement that cardiac catheterization is not useful in",
"patients with chronic MR in the following setting",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        &bull;&nbsp; For left ventriculography and hemodynamic measurements when valve surgery is not contemplated.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from Bonow RO, Carabello BA, Chatterjee K, et al. ACC/AHA 2006 guidelines for the management of patients with valvular heart disease. A report of the American College of Cardiology/American Heart Association Task Force on Practice Guidelines (Writing committee to revise the 1998 guidelines for the management of patients with valvular heart disease). J Am Coll Cardiol 2006; 48:e1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
