var title_f37_18_38188="Phenytoin poisoning - Rapid overview";
var content_f37_18_38188=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F56499&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F56499&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Phenytoin poisoning: Rapid overview",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <span style=\"color: #ff0000;\">",
"         To obtain emergent consultation with a medical toxicologist, call the United States Poison Control Network at 1-800-222-1222, or access the World Health Organization's list of international poison centers (",
"        </span>",
"        <a href=\"file://www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html\" target=\"_blank\">",
"         www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html",
"        </a>",
"        <span style=\"color: #ff0000;\">",
"         ).",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        General",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Phenytoin is an anticonvulsant; fosphenytoin is a prodrug of phenytoin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neurological signs are by far the most common manifestation of phenytoin overdose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Rapid IV administration can cause hypotension, bradyarrhythmia, and possibly asystole",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mild poisoning: horizontal nystagmus, unsteady gait, worsening coordination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe poisoning: slurred speech; depressed mental status (possibly coma)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Laboratory/diagnostic evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Phenytoin toxicity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Obtain phenytoin serum concentration",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Obtain serum albumin if phenytoin concentration and clinical findings inconsistent; hypoalbuminemia predisposes to toxicity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Routine testing in poisoned patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Fingerstick glucose if mental status altered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Acetaminophen and salicylate concentrations if possible suicide attempt",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        ECG to assess for poisoning by drugs that effect QRS or QTc intervals",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Pregnancy test in women of childbearing years",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer activated charcoal (1 g/kg up to 50 g) for symptomatic acute ingestions, unless airway is not protected or other contraindications exist",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Administer activated charcoal (1 g/kg up to 50 g) for asymptomatic patients who report significant ingestions if less than 1 to 2 hours after ingestion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Provide supportive care as needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Disposition",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients with acute toxicity or moderate to severe symptoms from chronic toxicity should be hospitalized",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Children may manifest signs of phenytoin toxicity at lower serum concentrations than adults and thus are more likely to require admission",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients with chronic toxicity may be discharged if they can ambulate safely and have someone to monitor them and assist with their activities of daily living until toxicity has resolved",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
