var title_f37_2_37932="Comparison PD solutions";
var content_f37_2_37932=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F51006&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F51006&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison of different peritoneal dialysis solutions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"2\" rowspan=\"1\">",
"        Dextrose",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"        Amino acid",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"        Icodextrin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Low Ca++",
"       </td>",
"       <td class=\"subtitle2\">",
"        High Ca++",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Osmolality (mOsm/kg)",
"       </td>",
"       <td>",
"        346, 396 and 485",
"       </td>",
"       <td>",
"        346, 396 and 485",
"       </td>",
"       <td>",
"        365",
"       </td>",
"       <td>",
"        282",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Molecular Weight (Dalton)",
"       </td>",
"       <td>",
"        182",
"       </td>",
"       <td>",
"        182",
"       </td>",
"       <td>",
"        100-200",
"       </td>",
"       <td>",
"        20,000",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Advantages",
"       </td>",
"       <td>",
"        <p>",
"         Well studied",
"        </p>",
"        <p>",
"         Most commonly used for a long time",
"        </p>",
"        <p>",
"         Side effect profile well known to most of the nephrologists",
"        </p>",
"        <p>",
"         Allow the use of oral calcium containing phosphate binders",
"        </p>",
"       </td>",
"       <td>",
"        Same as low calcium solutions except it may help bringing up serum calcium in patients with low calcium levels",
"       </td>",
"       <td>",
"        Can improve nutritional status in malnourished diabetic and/or patients with recurrent peritonitis",
"       </td>",
"       <td>",
"        <p>",
"         Sustained ultrafiltration for many hours",
"        </p>",
"        <p>",
"         Decreased solute absorption",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Disadvantages",
"       </td>",
"       <td>",
"        <p>",
"         Short lived ultrafiltration",
"        </p>",
"        <p>",
"         Metabolic complications like hyperinsulinemia, hyperglycemia, hyperlipidemia, and weight gain",
"        </p>",
"        <p>",
"         Can lead to low serum calcium and bone disease",
"        </p>",
"       </td>",
"       <td>",
"        Same as low calcium except it can limit the use of calcium containing oral phosphate binders",
"       </td>",
"       <td>",
"        <p>",
"         Expensive",
"        </p>",
"        <p>",
"         May increase nitrogen waste product in blood",
"        </p>",
"        <p>",
"         May cause/worsen acidosis",
"        </p>",
"       </td>",
"       <td>",
"        Increases serum levels of maltose, maltotriose, and oligopolysacharides",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Indications",
"       </td>",
"       <td>",
"        <p>",
"         Commonly used, first line peritoneal dialysis solution in all patients unless special circumstances exist",
"        </p>",
"        <p>",
"         High serum phosphorus and normal to high serum calcium",
"        </p>",
"       </td>",
"       <td>",
"        Used in patients who have low calcium levels with low calcium containing dialysis solutions",
"       </td>",
"       <td>",
"        <p>",
"         Malnourished diabetic patients or",
"        </p>",
"        <p>",
"         Malnourished patients with recurrent peritonitis",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Patients who lose ultrafiltration",
"        </p>",
"        <p>",
"         In patients with automated PD situation during the dry day to achieve sustained UF and increased solute and fluid removal",
"        </p>",
"        <p>",
"         In diabetic patients",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Dinesh Chatoth, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
