var content_f37_21_38236=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F65532&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F65532&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ACC/AHA/ESC guideline summary: Recommended doses of drugs (listed alphabetically) proven effective for pharmacological cardioversion of atrial fibrillation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Drug",
"      </td>",
"      <td class=\"subtitle1\">",
"       Route of administration",
"      </td>",
"      <td class=\"subtitle1\">",
"       Dosage&Delta;",
"      </td>",
"      <td class=\"subtitle1\">",
"       Potential adverse effects",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Amiodarone",
"      </td>",
"      <td>",
"       Oral",
"      </td>",
"      <td>",
"       <p>",
"        Inpatient: 1.2 to 1.8 g per day in divided dose until 10 g total, then 200 to 400 mg per day as single dose",
"       </p>",
"       <p>",
"        Outpatient 600 to 800 mg/day divided dose until 10 g total, then 200 to 400 mg per day maintenance",
"       </p>",
"      </td>",
"      <td rowspan=\"2\">",
"       Hypotension, bradycardia, QT prolongation, torsade de pointes (rare), GI upset, constipation, phlebitis (IV use)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Intravenous (IV)/oral",
"      </td>",
"      <td>",
"       5 to 7 mg/kg over 30 to 60 min, then 1.2 to 1.8 g per day continuous IV or in divided oral doses until 10 g total, then 200 to 400 mg per day maintenance",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Dofetilide",
"      </td>",
"      <td>",
"       Oral",
"      </td>",
"      <td>",
"       <p>",
"        <strong>",
"         Creatinine clearance (mL/min):",
"        </strong>",
"       </p>",
"       <p>",
"        Greater than 60: 500 mcg BID",
"       </p>",
"       <p>",
"        40 to 60: 250 mcg BID",
"       </p>",
"       <p>",
"        20 to 40: 125 mcg BID",
"       </p>",
"       <p>",
"        Less than 20: Contraindicated",
"       </p>",
"      </td>",
"      <td>",
"       QT prolongation, torsade de pointes; adjust dose for renal function, body size, and age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Flecainide",
"      </td>",
"      <td>",
"       Oral",
"      </td>",
"      <td>",
"       200 to 300 mg&bull;",
"      </td>",
"      <td rowspan=\"2\">",
"       Hypotension, rapidly conducting atrial flutter",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Intravenous",
"      </td>",
"      <td>",
"       1.5 to 3.0 m per kg over 10 to 20 min&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Ibutilide",
"      </td>",
"      <td>",
"       Intravenous",
"      </td>",
"      <td>",
"       1 mg over 10 min; repeat 1 mg when necessary",
"      </td>",
"      <td>",
"       QT prolongation, torsade de pointes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Propafenone",
"      </td>",
"      <td>",
"       Oral",
"      </td>",
"      <td>",
"       450 to 600 mg",
"      </td>",
"      <td rowspan=\"2\">",
"       Hypotension, rapidly conducting atrial flutter",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Intravenous",
"      </td>",
"      <td>",
"       1.5 to 2.0 mg per kg over 10 to 20 min&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Quinidine&loz;",
"      </td>",
"      <td>",
"       Oral",
"      </td>",
"      <td>",
"       0.75 to 1.5 g in divided doses over 6 to 12 h, usually with a rate-slowing drug",
"      </td>",
"      <td>",
"       QT prolongation, torsade de pointes, gastrointestinal upset, hypotension",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     &Delta; Dosages given in the table may differ from those recommended by the manufacturers.",
"     <br>",
"      &bull; Insufficient data are available on which to base specific recommendations for the use of one loading regimen over another for patients with ischemic heart disease or impaired left ventricular function, and these drugs should be used cautiously or not at all in such patients.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from Fuster V, Ryden LE, Cannom DS, et al. ACC/AHA/ESC guidelines for the management of patients with atrial fibrillation. J Am Coll Cardiol 2006; 48:e149.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
