var title_f37_23_38267="Criteria for OT and PT in acute hospital setting";
var content_f37_23_38267=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F86416&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F86416&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria to identify patients able to participate in occupational therapy/physical therapy treatment in the acute hospital setting",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"80%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Health characteristic",
"       </td>",
"       <td class=\"subtitle1\">",
"        Criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        Cognition",
"       </td>",
"       <td>",
"        Able to follow one-step commands",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oriented to person",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Remember one of three items",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"7\">",
"        Hemodynamic",
"       </td>",
"       <td>",
"        Mean arterial pressure 65 to 110 mm Hg&nbsp;(resting systolic blood pressure 85 to 160 mm Hg, and &lt;200&nbsp; mm Hg with exertion)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Heart rate 40 to 130 beats per min",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Respiratory rate 5 to 40 breaths per min",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Oxygen saturation &ge;88 percent, with or without supplemental O",
"        <sub>",
"         2",
"        </sub>",
"        and at rest or with activity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Change in vital signs with activity &lt;20 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hematocrit &gt;25 percent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No unstable angina (eg, change in ECG with activity)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Musculoskeletal stability",
"       </td>",
"       <td>",
"        Fracture/open wounds require explicit physician guidance with regards to physical activity (eg, weight-bearing status, orthoses/braces, etc.)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain",
"       </td>",
"       <td>",
"        No acute exacerbation of pain during OT/PT",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     OT: occupational therapy; PT: physical therapy; ECG: electrocardiogram.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
