" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of refractory cutaneous dermatomyositis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/24/38281/contributors\">",
