var title_f37_25_38300="ARA criteria for SLE PI";
var content_f37_25_38300=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F71430&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F71430&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    ARA criteria for diagnosis of systemic lupus erythematosus",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Criterion",
"      </td>",
"      <td class=\"subtitle1\">",
"       Definition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Malar rash",
"      </td>",
"      <td>",
"       Fixed erythema, flat or raised, over the malar eminences, tending to spare the nasolabial folds",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Discoid rash",
"      </td>",
"      <td>",
"       Erythematosus raised patches with adherent keratotic scaling and follicular plugging; atrophic scarring may occur in older lesions",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Photosensitivity",
"      </td>",
"      <td>",
"       Skin rash as a result of unusual reaction to sunlight, by patient history or physician observation",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Oral ulcers",
"      </td>",
"      <td>",
"       Oral or nasopharyngeal ulceration, usually painless, observed by a physician",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Arthritis",
"      </td>",
"      <td>",
"       Nonerosive arthritis involving 2 or more peripheral joints, characterized by tenderness, swelling, or effusion",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Serositis",
"      </td>",
"      <td>",
"       Pleuritis - convincing history of pleuritic pain or rub heard by a physician or evidence of pleural effusion",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Pericarditis - documented by EKG, rub or evidence of pericardial effusion",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Renal disorder",
"      </td>",
"      <td>",
"       Persistent proteinuria greater than 0.5 grams per day or greater than 3+ if quantitation not performed",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cellular casts - may be red cell, hemoglobin, granular, tubular, or mixed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Neurologic disorder",
"      </td>",
"      <td>",
"       Seizures",
"       <strong>",
"        OR",
"       </strong>",
"       psychosis - in the absence of offending drugs or known metabolic derangements (uremia, ketoacidosis, or electrolyte imbalance)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"4\">",
"       Hematologic disorder",
"      </td>",
"      <td>",
"       Hemolytic anemia - with reticulocytosis",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Leukopenia - less than 4000/mm3 total on two or more occasions",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lymphopenia - less than 1500/mm3 on two or more occasions",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Thrombocytopenia - less than 100,000/mm3 in the absence of offending drugs",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"4\">",
"       Immunologic disorders",
"      </td>",
"      <td>",
"       Positive antiphospholipid antibody",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Anti-DNA - antibody to native DNA in abnormal titer",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Anti-Sm - presence of antibody to Sm nuclear antigen",
"       <strong>",
"        OR",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       False positive serologic test for syphilis known to be positive for at least six months and confirmed by Treponema pallidum immobilization or fluorescent treponemal antibody absorption test",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Antinuclear antibody",
"      </td>",
"      <td>",
"       An abnormal titer of antinuclear antibody by immunofluorescence or an equivalent assay at any point in time and in the absence of drugs known to be associated with \"drug-induced lupus\" syndrome",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
