var title_f37_26_38315="Treatment of hyponatremia in SAH";
var content_f37_26_38315=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F65772&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F65772&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Protocol for management of hyponatremia in patients with subarachnoid hemorrage",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        For Na level &lt;133 mEq/L or a decrease of 6 mEq/L in 24 to 48 hours:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1. NaCl tabs 3 g PO/NGT every 6 h",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2. Initiate 3 percent NaCl infusion at 20 ml/h IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        3. Check serum Na every 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        a. If Na &lt;130 mEq/L:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Increase rate by 20 ml/h (max rate = 80 ml/h)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        If on hold at present, initiate 3 percent NaCl infusion at 20 ml/h IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        b. If Na = 130 to 135 mEq/L:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Increase rate by 10 ml/h (max rate = 80 ml/h)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        If on hold at present, initiate 3 percent NaCl infusion at 10 ml/h IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        c. If Na = 136 to 140 mEq/L:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        No change",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        d. If Na &ge;140 mEq/L:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Hold infusion",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Woo CH, Roa VA, Sheridan W, Flint A. Performance characteristics of a sliding-scale hypertonic saline infusion protocol for the treatment of acute neurologic hyponatremia. Neurocrit Care 2009; 11:228, with kind permission from Springer Science + Business Media B.V. Copyright &copy; 2009.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
