" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Valvar aortic stenosis in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/28/38345/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/28/38345/contributors\">",
"     David W Brown, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/28/38345/contributors\">",
"     John F Keane, MD",
"    </a>",
"    <br/>",
"   </div>",
