" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of endometrial hyperplasia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/3/37943/contributors\">",
