var title_f37_31_38395="Contraindications to coronary angiography";
var content_f37_31_38395=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F54367&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F54367&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Relative contraindications to coronary angiography",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Acute renal failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic renal failure secondary to diabetes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Active gastrointestinal bleeding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Unexplained fever, which may be due to infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Untreated active infection",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute stroke",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe uncontrolled hypertension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe symptomatic electrolyte imbalance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe lack of cooperation by patient due to",
"psychological or severe systemic illness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe concomitant illness that drastically shortens",
"life expectancy or increases risk of therapeutic interventions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Refusal of patient to consider definitive therapy such",
"as PTCA, CABG, or valve replacement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Digitalis intoxication",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Documented anaphylactoid",
"reaction to angiographic contrast media",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe peripheral vascular disease limiting vascular",
"access",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decompensated heart failure or acute pulmonary",
"edema&nbsp;&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Severe coagulopathy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Aortic valve endocarditis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PTCA: percutaneoas transluminal coronary angioplasty; CABG: coronaryartety bypass graft.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission by Scanlon, PJ, Faxon, DP, Audet, AM, et al, J Am Coll Cardiol 1999; 33:1756.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
