var title_f37_36_38476="HCC surveillance recommendations";
var content_f37_36_38476=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F68371&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F68371&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Groups for whom HCC surveillance is recommended or in whom the risk of HCC is increased, but in whom efficacy of surveillance has not been demonstrated",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Population group",
"       </td>",
"       <td class=\"subtitle1\">",
"        Annual incidence (percent per year) for which surveillance is considered to be cost-effective",
"       </td>",
"       <td class=\"subtitle1\">",
"        Incidence of HCC",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Surveillance recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Asian male hepatitis B carriers over age 40",
"       </td>",
"       <td>",
"        0.2",
"       </td>",
"       <td>",
"        0.4-0.6 percent per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Asian female hepatitis B carriers over age 50",
"       </td>",
"       <td>",
"        0.2",
"       </td>",
"       <td>",
"        0.3-0.6 percent per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatitis B carrier with family history of HCC",
"       </td>",
"       <td>",
"        0.2",
"       </td>",
"       <td>",
"        Incidence higher than without family history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        African/North American blacks with hepatitis B",
"       </td>",
"       <td>",
"        0.2",
"       </td>",
"       <td>",
"        HCC occurs at a younger age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cirrhotic hepatitis B carriers",
"       </td>",
"       <td>",
"        0.2-1.5",
"       </td>",
"       <td>",
"        3-8 percent per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatitis C cirrhosis",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"       <td>",
"        3-5 percent per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stage 4 primary biliary cirrhosis",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"       <td>",
"        3-5 percent per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Genetic hemachromatosis and cirrhosis",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"       <td>",
"        Unknown, but probably &gt;1.5 percent per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alpha 1-antitrypsin deficiency and cirrhosis",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"       <td>",
"        Unknown, but probably &gt;1.5 percent per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Other cirrhosis",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"       <td>",
"        Unknown",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Surveillance benefit uncertain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatitis B carriers younger than 40 (males) or 50 (females)",
"       </td>",
"       <td>",
"        0.2",
"       </td>",
"       <td>",
"        &lt;0.2 percent per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Hepatitis C and stage 3 fibrosis",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"       <td>",
"        &lt;1.5 percent per year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Non-cirrhotic NAFLD",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"       <td>",
"        &lt;1.5 percent per year",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HCC: hepatocellular carcinoma; NAFLD: nonalcoholic fatty liver disease.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Bruix J, Sherman M. Management of Hepatocellular Carcinoma: An Update. Hepatology 2010. Copyright &copy; 2010 John Wiley &amp; Sons.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
