var title_f37_38_38507="Contraindications exercise test";
var content_f37_38_38507=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F53217&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F53217&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=9\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Contraindications for exercise testing in children and adolescents",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Acute inflammatory cardiac disease (eg, pericarditis, myocarditis, acute rheumatic heart disease)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Uncontrolled heart failure",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Acute myocardial infarction",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Acute pulmonary disease (eg, acute asthma, pneumonia)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Severe systemic hypertension (eg, blood pressure greater than 240/120 mmHg)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Acute renal disease (eg, acute glomerulonephritis)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Acute hepatitis (within three months after onset)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Drug overdose affecting cardiorespiratory response to exercise (eg, digitalis toxicity, salicylism, quinidine toxicity)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Severe aortic stenosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Severe pulmonary stenosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Serious ventricular dysrhythmia, especially when associated with significant cardiac disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Coronary arterial diseases (anomalous left coronary artery, homozygous hypercholesterolemia, Kawasaki disease [acute phase])",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Severe pulmonary vascular disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Metabolic disorders (glycogenolysis types I and V)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hemorrhagic diseases",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Orthostatic hypotension",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Washington RL, Bricker JT, Alpert BS, et al. Guidelines for exercise testing in the pediatric age group. From the Committee on Atherosclerosis and Hypertension in Children, Council on Cardiovascular Disease in the Young, the American Heart Association. Circulation 1994; 90:2166 and James FW. Exercise testing in children and young adults: an overview. Cardiovasc Clin 1978; 9:187.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
