" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Secondary endocrine therapies for castration resistant prostate cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/39/38520/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/39/38520/contributors\">",
"     Nancy A Dawson, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/39/38520/contributors\">",
"     Section Editors",
