var title_f37_4_37964="Empiric oral antibiotics animal bites";
var content_f37_4_37964=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F75020&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F75020&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Empiric oral antibiotic therapy for animal bites",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Antibiotic agents",
"      </td>",
"      <td class=\"subtitle1\">",
"       Adults",
"      </td>",
"      <td class=\"subtitle1\">",
"       Children",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Agent of choice",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Amoxicillin-clavulanate",
"      </td>",
"      <td>",
"       875/125 mg twice daily",
"      </td>",
"      <td>",
"       20 mg/kg per dose (amoxicillin component) two times daily (maximum 875 mg amoxicillin and 125 mg clavulinic acid per dose) &bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       Alternate empiric regimens include:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_left\" colspan=\"3\">",
"       One of the following agents with activity against",
"       <em>",
"        P. multocida",
"       </em>",
"       :",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Doxycycline*",
"      </td>",
"      <td>",
"       100 mg twice daily",
"      </td>",
"      <td>",
"       Not recommended in children &lt;8 years of age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       TMP-SMX*",
"      </td>",
"      <td>",
"       1 double strength tablet twice daily",
"      </td>",
"      <td>",
"       4-5 mg/kg (trimethoprim component) per dose twice daily (maximum 160 mg trimethoprim per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Penicillin VK",
"      </td>",
"      <td>",
"       500 mg four times daily",
"      </td>",
"      <td>",
"       12.5 mg/kg per dose four times daily (maximum 500 mg per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Cefuroxime",
"      </td>",
"      <td>",
"       500 mg twice daily",
"      </td>",
"      <td>",
"       10 mg/kg per dose twice daily (maximum 500 mg per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Moxifloxacin",
"      </td>",
"      <td>",
"       400 mg once daily",
"      </td>",
"      <td>",
"       Use with caution in children &lt;18 years of age",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent3\" colspan=\"3\">",
"       <strong>",
"        PLUS",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle3_left\" colspan=\"3\">",
"       One of the following agents with anaerobic activity:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Metronidazole",
"      </td>",
"      <td>",
"       500 mg three times daily",
"      </td>",
"      <td>",
"       10 mg/kg per dose three times daily (maximum 500 mg per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Clindamycin*",
"      </td>",
"      <td>",
"       450 mg three times daily",
"      </td>",
"      <td>",
"       10 mg/kg per dose three times daily (maximum 450 mg per dose)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"3\">",
"       The following agents have poor activity against",
"       <em>",
"        P. multocida",
"       </em>",
"       and should be avoided:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Cephalexin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Dicloxacillin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"3\">",
"       Erythromycin",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * May also be active against MRSA (check susceptibility testing).",
"     <br>",
"      TMP-SMX: Trimethoprim-sulfamethoxazole",
"      <br>",
"       &bull; In patients with infected bites, the clinician may increase the dose to 45 mg/kg per dose (amoxicillin component) twice daily (maximum 875 mg amoxicillin and 125 mg clavulinic acid per dose).",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
