var title_f37_40_38528="Wall push-up exercise video";
var content_f37_40_38528=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic2\" style=\"width: 1070px;\">",
"  <div class=\"videoplayer\" h264=\"./images/PC/72241/Wall_push_up.mp4?title=Wall+push-up+exercise+video\" style=\"width:480px;height:336px\">",
"  </div>",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Strength exercise video: Wall push-up",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 373px; height: 306px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    These push-ups will strengthen your arms, shoulders, and chest. Try this exercise during a TV commercial break.",
"    <br>",
"     <ol>",
"      <li>",
"       Face a wall, standing a little farther than arm's length away, feet shoulder-width apart.",
"      </li>",
"      <li>",
"       Lean your body forward and put your palms flat against the wall at shoulder height and shoulder-width apart.",
"      </li>",
"      <li>",
"       Slowly breathe in as you bend your elbows and lower your upper body toward the wall in a slow, controlled motion. Keep your feet flat on the floor.",
"      </li>",
"      <li>",
"       Hold the position for one second.",
"      </li>",
"      <li>",
"       Breathe out and slowly push yourself back until your arms are straight.",
"      </li>",
"      <li>",
"       Repeat 10 to 15 times.",
"      </li>",
"      <li>",
"       Rest; then repeat 10 to 15 more times.",
"      </li>",
"     </ol>",
"     <div class=\"footnotes\">",
"     </div>",
"     <div class=\"reference\">",
"      Reproduced from: Go4Life. National Institute on Aging at the National Institutes of Health. Available at: file://go4life.niapublications.org (Accessed December 2, 2011).",
"     </div>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
