var title_f37_40_38539="Lynch syndrome SGO criteria";
var content_f37_40_38539=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F68560&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F68560&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=10\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Lynch syndrome risk assessment: Society of Gynecologic Oncologists guidelines",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tr>",
"          <td class=\"subtitle1_single\">",
"           Genetic risk assessment is recommended in women with the following characteristics (&gt;20-25 percent chance of having Lynch syndrome):",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Endometrial or ovarian cancer with a synchronous or metachronous colorectal cancer with the first cancer diagnosed prior to age 50",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Endometrial or colorectal cancer and meet the revised Amsterdam criteria",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           First or second degree relative with a known germline mutation in a mismatch repair gene",
"          </td>",
"         </tr>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tr>",
"          <td class=\"subtitle1_single\">",
"           Genetic risk assessment may be helpful in women with the following characteristics (&gt;5-10 percent chance of having Lynch syndrome):",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Endometrial or ovarian cancer with a synchronous or metachronous colorectal or other Lynch-associated cancer with the first cancer diagnosed at any age",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Endometrial or colorectal cancer diagnosed before age 50",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           Endometrial or colorectal cancer with two or more first or second degree relatives with Lynch-associated cancers",
"          </td>",
"         </tr>",
"         <tr>",
"          <td>",
"           First or second degree relative meets the above criteria",
"          </td>",
"         </tr>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Lancaster, JM, Powell, CB, Kauff, ND, et al. Gynecol Oncol 2007; 107:159.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
