" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Acute management of nephrolithiasis in children",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/41/38552/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
