" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Local treatment for primary soft tissue sarcoma of the extremities and chest wall",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/47/38650/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
