var title_f37_51_38715="Urinary patterns associated with different kidney diseases";
var content_f37_51_38715=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEPH%2F56160&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEPH%2F56160&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Urinary patterns associated with different kidney diseases",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Urinary pattern",
"       </td>",
"       <td class=\"subtitle1\">",
"        Kidney disease suggested by pattern",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hematuria with dysmorphic red blood cells, red blood cell casts, varying degrees of albuminuria",
"       </td>",
"       <td>",
"        Proliferative glomerulonephritis (eg, IgA nephropathy, ANCA-associated vasculitis, lupus nephritis)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Heavy albuminuria with minimal or absent hematuria",
"       </td>",
"       <td>",
"        Non-proliferative glomerulopathy (eg, diabetes, amyloidosis, membranous nephropathy, focal segmental glomerulosclerosis, minimal change)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Multiple granular and epithelial cell casts with free epithelial cells",
"       </td>",
"       <td>",
"        Acute tubular necrosis in a patient with underlying acute kidney injury",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Isolated pyuria",
"       </td>",
"       <td>",
"        Infection (bacterial, mycobacterial) or tubulointerstitial disease",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        Normal or near-normal urinalysis with few cells, no casts, and no or minimal proteinuria",
"       </td>",
"       <td>",
"        In presence of acute kidney injury: pre-renal azotemia, acute tubular necrosis, hypercalcemia, urinary obstruction, vascular diseases with glomerular ischemia (eg, systemic sclerosis, malignant hypertension), or extra-glomerular vascular disease (eg, cholesterol emboli, polyarteritis nodosa)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        In presence of chronic kidney disease: ischemic nephropathy, hypertensive nephrosclerosis, urinary obstruction",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
