" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Management of asymptomatic abdominal aortic aneurysm",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/53/38746/contributors\">",
