" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Mycobacterium avium complex (MAC) infections in HIV-infected patients",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/55/38777/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/55/38777/contributors\">",
"     Judith S Currier, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/55/38777/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/55/38777/contributors\">",
"     John G Bartlett, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/55/38777/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/55/38777/contributors\">",
"     Allyson Bloom, MD",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"mobipreview.htm?37/55/38777/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
"   <span>",
"    All topics are updated as new evidence becomes available and our",
"   </span>",
"   <a href=\"/home/editorial-policy\" target=\"_blank\">",
"    peer review process",
"   </a>",
"   <span>",
"    is complete.",
"   </span>",
"  </div>",
"  <div id=\"literatureReviewDate\">",
"   <span class=\"emphasis\">",
"    Literature review current through:",
"   </span>",
"   Oct 2013.",
"   <span class=\"pipeSpace\">",
