var title_f37_55_38780="Theophylline guidelines";
var content_f37_55_38780=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F53731&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F53731&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=4\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Maintenance dosage guidelines for theophylline in children older than six months and adults who have no risk factors for decreased theophylline clearance*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Variable",
"       </td>",
"       <td class=\"subtitle1\">",
"        Weight-adjusted and maximal dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"       <td class=\"subtitle1\">",
"        Adjustment to dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Initial dose",
"       </td>",
"       <td>",
"        ~10 mg/kg of body weight/day; maximum, 300 mg/day",
"       </td>",
"       <td>",
"        If initial dose is tolerated, increase the dose no sooner than 3 days later to the first dose increase.",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        First dose increase",
"       </td>",
"       <td>",
"        ~13 mg/kg/day; maximum 450 mg/day",
"       </td>",
"       <td>",
"        If the first dose increase is tolerated, increase the dose no sooner than 3 days later to the second dose increase.",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Second dose increase",
"       </td>",
"       <td>",
"        ~16 mg/kg/day; maximum 600 mg/day",
"       </td>",
"       <td>",
"        Measure the peak serum concentration after at least 3 days at the highest tolerated dose.&bull;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serum theophylline concentration (&micro;g/mL)",
"       </td>",
"       <td colspan=\"3\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &lt;10",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Increase approximately 25 percent.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        10 to 15",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Maintain dose if tolerated.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        15.1 to 19.9",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Consider a reduction of approximately 10 percent.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        20 to 25",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Withhold next dose, then resume treatment with next lower dose level.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &gt;25",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        Withhold next 2 doses, then resume treatment with initial dose or lower dose.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * For infants 6 weeks to 6 months of age, the initial daily dose is calculated according to the following regression equation: dose (in milligrams per kilogram per day) = (0.2) (age in weeks) + 5.0. Subsequent increases in the dose in this age group should be based on peak serum concentrations measured no sooner than three days after the start of therapy.",
"     <br>",
"      &bull; The length of time to the peak serum concentration depends on the rate of absorption, the rate of elimination, and the dosing interval.",
"      <br>",
"       &Delta; This decreases the likelihood of side effects due to fluctuations in the absorption or elimination rate that may result in serum concentrations above 20 &micro;g per milliliter and is especially important for patients who require doses higher than those used in the second dose increase.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Weinberger, M, Hendeles, L, N Engl J Med 1996; 334:1380.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
