" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Iron overload syndromes other than hereditary hemochromatosis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?37/60/38856/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/60/38856/contributors\">",
"     Stanley L Schrier, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?37/60/38856/contributors\">",
"     Bruce R Bacon, MD",
"    </a>",
"    <br/>",
"   </div>",
