var title_f37_61_38875="Young and Burgess classification of pelvic fractures";
var content_f37_61_38875=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F55023&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F55023&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Young and Burgess classification of pelvic fractures",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        LC: anterior injury = rami fractures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        LC I: sacral fracture on side of impact",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        LC II: crescent fracture on side of impact",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        LC III: type I or II injury on side of impact with contralateral open book injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        AP compression (APC): anterior injury = symphysis diastasis/rami fractures",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        APC I: minor opening of symphysis and SI joint anteriorly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        APC II: opening of anterior SI, intact posterior SI ligaments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        APC III: complete disruption of SI joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Vertical shear (VS) type:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Vertical displacement of hemipelvis with symphysis diastasis or rami fractures anteriorly, iliac wing, sacral facture, or SI dislocation posteriorly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\">",
"        Combined mechanism (CM) type: any combination of above injuries",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Sagi C. Lower extremity: Pelvic ring fractures. In: Rockwood and Green's Fractures in Adults,&nbsp;7th ed, Bucholz RW, Court-Brown CM, Heckman JD, Tornetta P (Eds), Lippincott Williams &amp; Wilkins, Philadelphia 2010. Copyright &copy; 2010 Lippincott Williams &amp; Wilkins.",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.lww.com\">",
"      file://www.lww.com",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
