var title_f37_61_38877="Digoxin and HF mortality";
var content_f37_61_38877=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F58166&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F58166&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 470px\">",
"   <div class=\"ttl\">",
"    Digoxin reduces death and hospitalization due to worsening HF",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 410px; height: 284px; background-image: url(data:image/gif;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
"    Among 6800 patients with heart failure randomized to therapy with digoxin or placebo, patients treated with digoxin had a significant decrease in the combined endpoints of death from HF and hospitalization for worsening HF (p&lt;0.001).",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: The Digitalis Investigation Group, N Engl J Med 1997; 336:525.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
