var title_f38_11_39099="Cincinnati Prehospital Stroke Scale";
var content_f38_11_39099=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F62856&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F62856&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cincinnati Prehospital Stroke Scale",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        The CPSS evaluates for facial palsy, arm weakness, and speech abnormalities. Items are scored as either normal or abnormal.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Facial droop",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The patient shows teeth or smiles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Normal:",
"        </strong>",
"        Both sides of face move equally.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Abnormal:",
"        </strong>",
"        One side of face does not move as well as the other.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Arm drift",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The patient closes their eyes and extends both arms straight out for 10 seconds",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Normal:",
"        </strong>",
"        Both arms move the same, or both arms do not move at all.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Abnormal:",
"        </strong>",
"        One arm either does not move, or one arm drifts down compared to the other.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Speech",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        The patient repeats \"The sky is blue in Cincinnati\"",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Normal:",
"        </strong>",
"        The patient says correct words with no slurring of words.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Abnormal:",
"        </strong>",
"        The patient slurs words, says the wrong words, or is unable to speak.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Kothari RU, Pancioli A, Liu T, et al. Cincinnati Prehospital Stroke Scale: reproducibility and validity. Ann Emerg Med 1999; 33:373. Illustration used with the permission of Elsevier Inc. All rights reserved.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
