"       <td class=\"indent1\">",
"        Ketoprofen",
"       </td>",
"       <td>",
"        0.1",
"       </td>",
"       <td>",
"        0.08",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ibuprofen",
"       </td>",
"       <td>",
"        0.6",
"       </td>",
"       <td>",
"        0.70",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Indomethacin",
"       </td>",
"       <td>",
"        0.7",
"       </td>",
"       <td>",
"        0.85",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Naproxen",
"       </td>",
"       <td>",
"        1.0",
"       </td>",
"       <td>",
"        0.52",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Piroxicam",
"       </td>",
"       <td>",
"        1.3",
"       </td>",
"       <td>",
"        0.87",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ketoralac",
"       </td>",
"       <td>",
"        1.5",
"       </td>",
"       <td>",
