var title_f38_24_39308="Definitions of esophageal pressure topography metrics";
var content_f38_24_39308=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F86119&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F86119&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Definitions of esophageal pressure topography metrics",
"    <sup>",
"     [1]",
"    </sup>",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"40%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        EPT metric",
"       </td>",
"       <td class=\"subtitle1\">",
"        Definition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Conventional manometry correlate",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         IRP",
"        </strong>",
"        (mmHg)",
"       </td>",
"       <td>",
"        Average minimum EGJ pressure for four contiguous or noncontiguous seconds of relaxation within 10 seconds of swallow (upper sphincter relaxation) (&gt;15 mmHg is abnormal)",
"       </td>",
"       <td>",
"        LES relaxation measured with best estimate position through the EGJ (&gt;8 mmHg is abnormal)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         Peristaltic breaks",
"        </strong>",
"        (cm)",
"       </td>",
"       <td>",
"        Gap in the 20 mmHg isobaric contour of the peristaltic contraction between the UES and EGJ, measured in axial length",
"        <ul>",
"         <li>",
"          Small break: 2 to 5 cm in length",
"         </li>",
"         <li>",
"          Large break: &gt;5 cm in length",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Utilizes the 30 mmHg amplitude in positions 3 and 8 cm above the LES to distinguish a swallow as ineffective",
"        <ul>",
"         <li>",
"          Ineffective &lt;30 mmHg at 3 and/or 8 cm recording sites above the LES",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         DCI",
"        </strong>",
"        (mmHg-s-cm)",
"       </td>",
"       <td>",
"        Amplitude x duration x length (mmHg-s-cm) of the distal esophageal contraction greater than 20 mmHg measured between the proximal and distal pressure troughs",
"        <ul>",
"         <li>",
"          Hypertensive: mean DCI of 10 swallows &gt;5000 mmHg-s-cm",
"         </li>",
"         <li>",
"          Hypercontractile: at least one swallow with a DCI &gt;8000 mmHg-s-cm",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        Average of 10 swallows at two recording sites positioned 3 and 8 cm above the LES",
"        <ul>",
"         <li>",
"          Nutcracker &gt;180 mmHg",
"         </li>",
"         <li>",
"          Revised definitions &gt;220 to 260 mmHg",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <strong>",
"         CFV",
"        </strong>",
"        (cm/second)",
"       </td>",
"       <td>",
"        Slope of the tangent to the 30 mmHg isobaric contour between the proximal pressure trough and the CDP (&gt;9 cm/second is abnormal)",
"       </td>",
"       <td>",
"        Peristaltic progression from the UES to the LES at a rate of 2 to 8 cm/second",
"        <ul>",
"         <li>",
"          Greater than 8 cm/second is abnormal",
"         </li>",
"        </ul>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         DL",
"        </strong>",
"        (seconds)",
"       </td>",
"       <td>",
"        Interval between UES relaxation and the CDP (&lt;4.5 seconds is abnormal)",
"       </td>",
"       <td>",
"        No correlate in conventional manometry",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     EPT: esophageal pressure topography; IRP: integrated relaxation pressure; EGJ: esophagogastric junction; LES: lower esophageal sphincter; UES: upper esophageal sphincter; DCI: distal contractile integral; CFV: contractile front velocity; CDP: contractile deceleration point; DL: distal latency.",
"    </div>",
"    <div class=\"reference\">",
"     References:",
"     <br/>",
"     <ol>",
"      <li>",
"       Spechler SJ, Castell DO. Classification of oesophageal motility abnormalities. Gut 2001; 49:145.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
