var title_f38_25_39323="Grading GI MALT lymphoma";
var content_f38_25_39323=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F78881&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F78881&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Histologic scoring of marginal zone (MALT) lymphoma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"       <td class=\"subtitle1\">",
"        Histologic features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        0",
"       </td>",
"       <td>",
"        Normal",
"       </td>",
"       <td>",
"        Scattered plasma cells in lamina propria; no lymphoid follicles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        Chronic active gastritis",
"       </td>",
"       <td>",
"        Small clusters of lymphocytes in lamina propria; no lymphoid follicles; no LELs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        Chronic active gastritis with florid lymphoid follicle formation",
"       </td>",
"       <td>",
"        Prominent lymphoid follicles with surrounding mantle zone and plasma cells; no LELs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3",
"       </td>",
"       <td>",
"        Suspicious lymphoid infiltrate in lamina propria, probably reactive",
"       </td>",
"       <td>",
"        Lymphoid follicles surrounded by small lymphocites that infiltrate diffusely in lamina propria and occasionally into epithelium",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4",
"       </td>",
"       <td>",
"        Suspicious lymphoid infiltrate in lamina propria, probably lymphoma",
"       </td>",
"       <td>",
"        Lymphoid follicles surrounded by CCL cells that infiltrate diffusely in lamina propria and into epithelium in small groups",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5",
"       </td>",
"       <td>",
"        Low-grade B-cell lymphoma of MALT",
"       </td>",
"       <td>",
"        Presence of dense diffuse infiltrate of CCL cells in lamina propria with prominent LELs",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CCL: centrocyte-like lesion; LEL: lymphoepithelial lesion.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Wotherspoon, AC, Dogliogi, C, Diss, TC, et al, Lancet 1993; 342:575.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
