var content_f38_29_39388=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F68759&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F68759&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical findings in women with vaginitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"5\" width=\"20%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Parameter",
"       </td>",
"       <td class=\"subtitle1\">",
"        Normal findings",
"       </td>",
"       <td class=\"subtitle1\">",
"        Vulovaginal candidiasis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Bacterial vaginosis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Trichomoniasis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Symptoms",
"       </td>",
"       <td>",
"        None or mild, transient",
"       </td>",
"       <td>",
"        Pruritus, soreness, dyspareunia",
"       </td>",
"       <td>",
"        Malodorous discharge, no dyspareunia",
"       </td>",
"       <td>",
"        Malodorous discharge, burning, postcoital bleeding, dyspareunia, dysuria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Signs",
"       </td>",
"       <td>",
"        Normal vaginal discharge consists of 1 to 4 mL fluid (per 24 hours), which is white or transparent, thin or thick, and mostly odorless",
"       </td>",
"       <td>",
"        Vulvar erythema and/or edema. Discharge may be white and clumpy and may or may not adhere to vagina.",
"       </td>",
"       <td>",
"        Off-white/gray thin discharge that coats the vagina",
"       </td>",
"       <td>",
"        Thin green-yellow discharge, vulvovaginal erythema",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Vaginal pH",
"       </td>",
"       <td>",
"        4.0-4.5",
"       </td>",
"       <td>",
"        4.0-4.5",
"       </td>",
"       <td>",
"        &gt;4.5",
"       </td>",
"       <td>",
"        5.0-6.0",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Amine test",
"       </td>",
"       <td>",
"        Negative",
"       </td>",
"       <td>",
"        Negative",
"       </td>",
"       <td>",
"        Positive (in 70-80 percent of patients)",
"       </td>",
"       <td>",
"        Often positive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Saline microscopy",
"       </td>",
"       <td>",
"        PMN:EC ratio &lt;1; rods dominate; squames +++",
"       </td>",
"       <td>",
"        PMN:EC ratio &lt;1; rods dominate; squames +++; pseudohyphae (present in about 40 percent of patients); budding yeast for nonalbicans Candida",
"       </td>",
"       <td>",
"        PMN:EC &lt;1; loss of rods; increased coccobacilli; clue cells comprise at least 20 percent of epithelial cells (present in &gt;90 percent of patients)",
"       </td>",
"       <td>",
"        PMN ++++; mixed flora; motile trichomonads (present in about 60 percent of patients)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        10 percent potassium hydroxide microscopy",
"       </td>",
"       <td>",
"        Negative",
"       </td>",
"       <td>",
"        Pseudohyphae (in about 70 percent of patients)",
"       </td>",
"       <td>",
"        Negative",
"       </td>",
"       <td>",
"        Negative",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Other tests",
"       </td>",
"       <td>",
"        &ndash;",
"       </td>",
"       <td>",
"        Culture if microscopy nondiagnostic",
"       </td>",
"       <td>",
"        Culture of no value",
"       </td>",
"       <td>",
"        Culture or rapid antigen/nucleic acid amplification tests if microscopy nondiagnostic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Differential diagnosis",
"       </td>",
"       <td>",
"        Physiologic leukorrhea",
"       </td>",
"       <td>",
"        Contact irritant or allergic vulvar dermatitis, chemical irritation, focal vulvitis (vulvodynia)",
"       </td>",
"       <td>",
"        Elevated pH in trichomoniasis, atrophic vaginitis, and desquamative inflammatory vaginitis",
"       </td>",
"       <td>",
"        Purulent vaginitis, desquamative inflammatory vaginitis, atrophic vaginitis, erosive lichen planus",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     PMN: polymorphonuclear leukocytes; EC: vaginal epithelial cells.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
