var title_f38_31_39419="Renal changes with aging";
var content_f38_31_39419=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F68244&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F68244&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Renal changes with aging",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Anatomic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decrease in renal mass, mostly from cortex",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased renal fat and fibrosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sclerosis of cortical nephrons with longest loops of Henle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Functional",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased renal blood flow",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Impaired vasodilation in response to dopamine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased creatinine clearance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Impaired sodium excretion and conservation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased potassium excretion and conservation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased concentrating and diluting capacity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Impaired excretion of acid loads",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased serum renin and aldosterone",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Altered intrarenal nitric oxide actions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased dependence on renal prostaglandins to maintain intrarenal perfusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased vitamin D activation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Increased vulnerability to dye, ischemia, or other insults",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Impaired recovery after insults",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Taffet GE. Physiology of aging. In: Geriatric Medicine: An Evidence-Based Approach, Cassel CK, Leipzig RM, Cohen HJ, et al (Eds), 4th ed, Springer, New York 2003.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
