var title_f38_35_39483="Causes of ST segment elevation";
var content_f38_35_39483=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F81757&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F81757&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of ST segment elevation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Myocardial ischemia or infarction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Noninfarction, transmural ischemia (Prinzmetal's angina pattern or acute takotsubo syndrome)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Acute myocardial infarction (MI) usually due to coronary atherosclerosis or occasionally to other causes (eg, acute takotsubo syndrome)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Post-MI (ventricular aneurysm pattern)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute pericarditis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Abnormal early repolarization syndromes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Normal variants (including benign early repolarization)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start_left\">",
"        Left ventricular hypertrophy or left bundle branch block (V1-V2 or V3)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Myocarditis (may look like MI or pericarditis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Massive pulmonary embolism (leads V1-V2 in occasional cases)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Brugada-type patterns (V1-V3 with right bundle branch block-appearing morphology)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Myocardial tumor",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Myocardial trauma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hyperkalemia (only leads V1 and V2)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypothermia (J wave/Osborn wave)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypercalcemia (rarely)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Post-DC cardioversion (rarely)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
