" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical implications of ischemic preconditioning",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
