var title_f38_40_39563="Treatment of Candida osteoarticular infections";
var content_f38_40_39563=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F87679&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F87679&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of Candida osteoarticular infections in adults",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Condition or treatment group",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"3\">",
"        Therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Primary",
"       </td>",
"       <td class=\"subtitle2\">",
"        Alternative",
"       </td>",
"       <td class=\"subtitle2\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Osteomyelitis",
"       </td>",
"       <td>",
"        Fluconazole 400 mg (6 mg/kg) orally daily for 6 to 12 months or a lipid formulation of amphotericin B 3 to 5 mg/kg IV daily for several weeks, then fluconazole for 6 to 12 months",
"       </td>",
"       <td>",
"        An echinocandin* or amphotericin B deoxycholate 0.5 to 1 mg/kg IV daily for several weeks then fluconazole for 6 to 12 months",
"       </td>",
"       <td>",
"        Duration of therapy usually is prolonged (6 to 12 months). Surgical debridement is frequently necessary.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Septic arthritis",
"       </td>",
"       <td>",
"        Fluconazole 400 mg (6 mg/kg) orally daily for at least six weeks or a lipid formulation of amphotericin B 3 to 5 mg/kg IV daily for several weeks, then fluconazole to completion",
"       </td>",
"       <td>",
"        An echinocandin* or amphotericin B deoxycholate 0.5 to 1 mg/kg IV daily for several weeks, then fluconazole to completion",
"       </td>",
"       <td>",
"        Duration of therapy is usually for at least six weeks, but few data are available. Surgical debridement is recommended for all cases. For infected prosthetic joints, removal is recommended for most cases.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Echinocandin dosing in adults is as follows: anidulafungin, 200 mg IV loading dose, then 100 mg IV once daily; caspofungin, 70 mg IV loading dose, then 50 mg IV once daily; and micafungin, 100 mg IV once daily.",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Pappas PG, Kauffman CA, Andes D, et al. Clinical practice guidelines for the management of candidiasis: 2009 update by the Infections Diseases Society of America. Clin Infect Dis 2009; 48:503. Copyright &copy; 2009 University of Chicago Press.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
