var title_f38_43_39612="Pressure sore status tool";
var content_f38_43_39612=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F72135&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F72135&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pressure sore status tool",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Stage",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Exudate amount",
"       </td>",
"       <td>",
"        1 = None&nbsp;&nbsp;&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 = Scant",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 = Small",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 = Moderate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5 = Large",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Skin color surrounding wound",
"       </td>",
"       <td>",
"        1 = Pink or normal for ethnic group",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 = Bright red and/or blanches to touch",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 = White or gray pallor or hypopigmented",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 = Dark red or purple and/or nonblanchable",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5 = Black or hypopigmented",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Peripheral tissue edema",
"       </td>",
"       <td>",
"        1 = Minimal swelling around wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 = Nonpitting edema extends &lt;4 cm around wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 = Nonpitting edema extends &ge;4 cm around wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 = Pitting edema extends &lt;4 cm around wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5 = Crepitus and/or pitting edema extends &ge;4 cm",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Peripheral tissue induration",
"       </td>",
"       <td>",
"        1 = Minimal firmness around wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 = &lt;2 cm around wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 = 2 to 4 cm extending &lt;50 percent around wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 = 2 to 4 cm extending &ge;50 percent around wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5 = &gt;4 cm in any area",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Granulation tissue",
"       </td>",
"       <td>",
"        1 = Skin intact or partial-thickness wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 = Bright, beefy red; 75 to 100 percent of wound filled and/or tissue overgrowth",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 = Bright, beefy red; &lt;75 percent and &gt;25 percent of wound filled",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 = Pink and/or dull, dusky red and/or fills &le;25 percent of wound",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5 = No granulation tissue present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"5\">",
"        Epithilization",
"       </td>",
"       <td>",
"        1 = 100 percent of wound covered, surface intact",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        2 = 75 to 100 percent of wound covered and/or epithelial tissue extends &gt;0.5 cm into wound bed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        3 = 50 to 75 percent of wound covered and/or epithelial tissue extends &lt;0.5 cm into wound bed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        4 = 25 to 50 percent of wound covered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        5 = &lt;25 percent of wound covered",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Bates-Jensen, BM, Adv Wound Care 1998; 10:65.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
